import React from 'react';

class Register extends React.Component{
    constructor() {
        super();
        this.state = {
            email: '',
            password: '',
            name:''
        }
    }
    onNameChange=(event)=>{
        console.log(event.target.value);
        this.setState({
            name:event.target.value
        })
    }
    onEmailChange = (event) => {
        console.log(event.target.value)
        this.setState({
            email: event.target.value
        })
    }
    onPasswordChange = (event) => {
        console.log(event.target.value);
        this.setState({
            password: event.target.value
        })
    }

    onSubmitRegister = () => {
        const{email,password,name}=this.state;
        console.log(email);
        console.log(password);
        console.log(name);
        fetch('http://localhost:5000/register', {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: JSON.stringify({
                email: email,
                password: password,
                name:name
            })
        })
            .then(response => response.json()
                .then(user => {
                    if (user.id) {
                        this.props.loadUser(user);
                        this.props.onRouteChange('home');
                    }
                }))
    }
    render(){
        // const {onRouteChange}= this.props;
    return (
        <article className="br3 ba b--black-10 mv4 w-50-m w-25-l mw6 shadow-5 center">
        <main className="pa4 black-80">
            <div className="measure">
                <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                    <legend className="f4 fw6 ph0 mh0">Register</legend>
                    <div className="mt3">
                        <label className="db fw6 lh-copy f6" >Name</label>
                        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="name" name="name" id="name"
                        onChange={this.onNameChange} />
                    </div>
                    <div className="mt3">
                        <label className="db fw6 lh-copy f6" >Email</label>
                        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address" id="email-address" onChange={this.onEmailChange}/>
                    </div>
                    <div className="mv3">
                        <label className="db fw6 lh-copy f6">Password</label>
                        <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password" id="password" onChange={this.onPasswordChange}/>
                    </div>
                    
                </fieldset>
                <div className="register">
                    <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Register" onClick={this.onSubmitRegister} />
                </div>

            </div>
        </main>
        </article>
    )
    }
}

export default Register;
