import React from 'react';

class SignIn extends React.Component {
    constructor() {
        super();
        this.state = {
            emailChange: '',
            passwordChange: ''
        }
    }

    onEmailChange = (event) => {
        console.log(event.target.value)
        this.setState({
            emailChange: event.target.value
        })
    }
    onPasswordChange = (event) => {
        console.log(event.target.value);
        this.setState({
            passwordChange: event.target.value
        })
    }

    onSubmitSignIn = () => {
        fetch('https://pacific-tundra-07760.herokuapp.com/signIn', {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: JSON.stringify({
                email: this.state.emailChange,
                password: this.state.passwordChange
            })
        })
            .then(response => response.json()
                .then(user => {
                    console.log(user);
                    if (user.id) {
                        this.props.loadUser(user);
                        this.props.onRouteChange('home');
                    }
                }))
    }
    render() {
        const { onRouteChange } = this.props
        return (
            <article className="br3 ba b--black-10 mv4 w-50-m w-25-l mw6 shadow-5 center">
                <main className="pa4 black-80">
                    <div className="measure">
                        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                            <legend className="f4 fw6 ph0 mh0">Sign In</legend>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" >Email</label>
                                <input onChange={this.onEmailChange} className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address" id="email-address" />
                            </div>
                            <div className="mv3">
                                <label className="db fw6 lh-copy f6">Password</label>
                                <input onChange={this.onPasswordChange} className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password" id="password" />
                            </div>

                        </fieldset>
                        <div className="">
                            <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Sign in" onClick={this.onSubmitSignIn} />
                        </div>
                        <div className="lh-copy mt3">
                            <p href="#0" className="f6 link dim black db pointer" onClick={() => onRouteChange('register')}>Register</p>
                        </div>

                    </div>
                </main>
            </article>
        )
    }
}

export default SignIn;
